mapper.select:
select a.id, a.advertisement_main_text, a.advertisement_sub_text, a.advertisement_status, a.advertisement_request_status, a.advertise_start_datetime, a.advertise_end_datetime, coalesce(max(p.payment_price),0) as payment_price from tbl_advertisement a join tbl_payment p on a.id = p.advertisement_id where a.company_id = #{companyId}
and ( a.advertisement_main_text like concat('%', #{keyword}, '%') )
group by a.id, a.advertisement_main_text, a.advertisement_sub_text, a.advertisement_request_status, a.advertise_start_datetime, a.advertise_end_datetime, a.created_datetime order by a.created_datetime desc limit #{criteria.count} offset #{criteria.offset}

select count(distinct a.id) from tbl_advertisement a left join tbl_payment p on a.id = p.advertisement_id where a.company_id = #{companyId}
and a.advertisement_request_status = 'accept'
and ( a.advertisement_main_text like concat('%', #{keyword}, '%') )
