mapper.select:
select re.id as request_id, re.request_intern_status, re.created_datetime as request_datetime, u.id as user_id, u.user_name, u.user_email, u.user_phone, en.id as intern_notice_id, en.intern_notice_title, en.company_id from tbl_request_intern re join tbl_user u on re.member_id = u.id join tbl_intern_notice en on re.intern_notice_id = en.id where en.id = #{internNoticeId} and u.user_status = 'active'
and re.request_intern_status = #{status}
order by re.id desc limit #{criteria.count} offset #{criteria.offset}
