mapper.update:
update tbl_company
set
    company_name = #{companyName},
    company_info = #{companyInfo},
    company_url = #{companyUrl}
where user_id = #{companyId}

update tbl_user
set user_name = #{ceoName}
where id = #{companyId}

update tbl_company_sector_category
set company_sector_id = (
    select id from tbl_company_sector
    where company_sector_name = #{companySectorName}
)
where company_id = #{companyId};

update tbl_company_scale_category_bridge
set company_scale_category_id = (
    select id from tbl_company_scale_category
    where company_scale_name = #{companyScaleName}
)
where company_id = #{companyId};
